		}		
			
		$1 = $script:startX * $sizeMultiplier
		$2 = $script:startY * $sizeMultiplier
		$3 = ($X * $sizeMultiplier - ($script:startX * $sizeMultiplier))
		$4 =($Y * $sizeMultiplier - ($script:startY * $sizeMultiplier))
		$1 = $1 -as [int]
